# Day 1 - Part 1


def calculateFloor(floor_instruction):
    current_floor = 0
    for floor_code in floor_instruction:
        if floor_code == "(":
            current_floor += 1
        elif floor_code == ")":
            current_floor -= 1
        else:
            raise ValueError("Invalid Character")
    return current_floor


