# Day 1 - Part 2


def get_basement_position(floor_instruction):
    current_floor = 0
    current_position = 0
    for floor_code in floor_instruction:
        current_position += 1
        if floor_code == "(":
            current_floor += 1
        elif floor_code == ")":
            current_floor -= 1
            if current_floor == -1:
                return current_position
        else:
            raise ValueError("Invalid Character")
    return -1


